import {React, useEffect, useState} from 'react';

import './Key.css'
// import Global from '../Global.js'

const Piano_Key = ({ note }) => {
    const [pressedNote, setPressedNote] = useState("unpressed")
    const [color, setColor] = useState(null)
    const keyPress = () => {
        setPressedNote("pressed")
        setTimeout(()=>{setPressedNote("unpressed")}, 1)
        console.log("pressedNote: ", note)
        console.log("pressed status: ", pressedNote)
    }



    useEffect(()=>{
        const key_color = () => {
            if (note.length > 1) {
                setColor("black")
            } else {
                setColor("white")
            }
        }
        key_color()
    },[note.length])

    // useEffect(()=>{
    //     sendUserAnswerUp(pressedNote)

    // },[pressedNote
    //     , sendUserAnswerUp
    // ])

    return (
        <div className={color} onClick={keyPress}>{note}</div>
    )
}

export default Piano_Key
