    const [play, setPlay] = useState(true)
    // const [quit, setQuit] = useState(false)
    const handleClick = () => {
        setPlay(!play)
        // setQuit(!quit)
    }

    return(
        (play === true ?
            <button onClick={handleClick}> PLAY</button>
            :
            <Rnote /> 
        )    
    )
}

